(105, 'Xây dựng mô hình giảng bài tiếp cận tâm lý học sinh', 'xay-dng-mo-hinh-ging-bai-tip-cn-tam-ly-hc-sinh', '', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '', 0, 3, 0, 22, '2011-10-22 01:39:59', 62, '', '2012-04-10 12:20:46', 62, 0, '0000-00-00 00:00:00', '2011-10-22 01:38:43', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 3, 0, 4, '', '', 0, 74, 'robots=\nauthor='),
